mapper.insert:
insert into user
(email, password, birthday, skill, position_uid, nickname) 
VALUES(
	#{email},#{password},#{birthday},#{skill},#{position_uid},#{nickname}
)

mapper.update:
update user 
   set 	 email 	= #{email}
		,password	= #{password}			
		,nickname	= #{nickname}			
		,birthday	= #{birthday}			
		,skill	= #{skill}			
		,position_uid	= #{position_uid}			
		,nickname	= #{nickname}			
		,city_code	= #{city_code}			
		,state_code	= #{state_code}			
		,dong_code	= #{dong_code}			
		,comment	= #{comment}			
 where uid= #{uid}

update user 
   set 	 team_uid 	= #{team_uid}			
 where uid= #{uid};
 delete from team_apply where user_uid = #{uid};

update user 
   set 	 team_uid 	= null		
 where uid= #{uid}

mapper.insert:
insert into team_apply
(team_uid, user_uid, comment) 
VALUES(
	#{team_uid},#{uid},#{comment}
)
