mapper.insert:
insert into team
(team_name, team_intro, captain_uid) 
VALUES(
	#{team_name},#{team_intro},#{captain_uid}
)

mapper.update:
update team 
   set 	 team_name 	= #{team_name}
		,team_intro	= #{team_intro}			
		,captain_uid	= #{captain_uid}					
 where uid= #{uid}

mapper.delete:
delete from team where uid = #{uid};
update user 
	set team_uid = null
where team_uid = #{uid}
