mapper.insert:
insert into user
(email, password, birthday, skill, position_uid, nickname) 
VALUES(
	#{email},#{password},#{birthday},#{skill},#{position_uid},#{nickname}
)

mapper.update:
update user 
   set 	 email 	= #{email}
		,password	= #{password}			
		,nickname	= #{nickname}			
		,birthday	= #{birthday}			
		,skill	= #{skill}			
		,position_uid	= #{position_uid}			
		,nickname	= #{nickname}			
		,city_code	= #{city_code}			
		,state_code	= #{state_code}			
		,dong_code	= #{dong_code}			
		,comment	= #{comment}			
 where uid= #{uid}

update team set player_num = player_num+1 where uid =  #{team_uid};
update user 
   set 	 team_uid 	= #{team_uid}			
 where uid= #{uid};
 delete from apply_team where user_uid = #{uid};

update team set player_num = player_num-1 where uid =  #{team_uid};
update user 
   set 	 team_uid 	= 0		
 where uid= #{uid}

mapper.insert:
insert into apply_team
(team_uid, user_uid, comment) 
VALUES(
	#{team_uid}, #{user_uid}, #{comment}
)
